import { decodeSencode, validateSencode, decodeURL, validateURL } from '@sudoku/sencode';

export default class PuzzleParser {

  static parse(input) {
    if (validateURL(input)) {
      return decodeURL(input);
    } else if (validateSencode(input)) {
      return decodeSencode(input);
    } else {
      throw new Error('Invalid custom Sudoku puzzle');
    }
  }
}